// import React from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const HistoricalWeather = ({ data }) => {
  if (!data || data.length === 0) {
    return null;
  }

  const chartData = {
    labels: data.map(day => new Date(day.date).toLocaleDateString('en-US', { weekday: 'short' })).reverse(),
    datasets: [
      {
        label: 'Max Temperature (째C)',
        data: data.map(day => day.day.maxtemp_c).reverse(),
        borderColor: 'rgba(255, 99, 132, 1)',
        backgroundColor: 'rgba(255, 99, 132, 0.2)',
      },
      {
        label: 'Min Temperature (째C)',
        data: data.map(day => day.day.mintemp_c).reverse(),
        borderColor: 'rgba(54, 162, 235, 1)',
        backgroundColor: 'rgba(54, 162, 235, 0.2)',
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Past 7 Days Temperature Trend',
      },
    },
  };

  return (
    <div className="mt-8">
      <h2 className="text-2xl font-bold text-center mb-4">Historical Weather</h2>
      <div className="bg-white dark:bg-gray-800 p-4 rounded-lg shadow-md">
        <Line data={chartData} options={options} />
      </div>
    </div>
  );
};

export default HistoricalWeather;
