// import React from 'react';

const Forecast = ({ data }) => {
  if (!data) {
    return null;
  }

  return (
    <div className="mt-8">
      <h2 className="text-2xl font-bold text-center mb-4">5-Day Forecast</h2>
      <div className="grid grid-cols-1 md:grid-cols-5 gap-4">
        {data.map((day) => (
          <div key={day.date} className="bg-white dark:bg-gray-800 p-4 rounded-lg shadow-md text-center">
            <p className="font-bold">{new Date(day.date).toLocaleDateString('en-US', { weekday: 'short' })}</p>
            <img src={day.day.condition.icon} alt={day.day.condition.text} className="w-16 h-16 mx-auto" />
            <p>{day.day.condition.text}</p>
            <p className="font-bold">{day.day.maxtemp_c}°C / {day.day.mintemp_c}°C</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Forecast;
