// import React from 'react';

const CurrentWeather = ({ data }) => {
  if (!data) {
    return null;
  }

  const { location, current } = data;

  return (
    <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-md text-center">
      <h2 className="text-2xl font-bold">{location.name}, {location.country}</h2>
      <div className="flex items-center justify-center my-4">
        <img src={current.condition.icon} alt={current.condition.text} className="w-20 h-20" />
        <div className="text-5xl font-bold ml-4">{current.temp_c}Â°C</div>
      </div>
      <p className="text-xl">{current.condition.text}</p>
      <div className="flex justify-around mt-4">
        <div>
          <p className="font-bold">Humidity</p>
          <p>{current.humidity}%</p>
        </div>
        <div>
          <p className="font-bold">Wind Speed</p>
          <p>{current.wind_kph} kph</p>
        </div>
      </div>
    </div>
  );
};

export default CurrentWeather;
